mapper.select:
select * from (
	select c.*,rownum r from (
		select * from account 




				and idcard_no=#{idcardNo}


				and real_name=#{realName}


				and login_name=#{loginName}



				and status=#{status}



		order by account_id
	) c
) where r<#{begin} and r>#{end}

select count(*) from account



				and idcard_no=#{idcardNo}


				and real_name=#{realName}


				and login_name=#{loginName}



				and status=#{status}
